define(['jsonic/jsonic-min'], function () {
  var THEME_NAME = 'default'
  function render () {
    $('div[data-code-plugin-dirtree]').each(function (index, el) {
      var $el = $(el)
      try {
        var code = jsonic($.decodeHTML($el.html()))
        $el
          .html('').addClass('code-plugin-dirtree')
          .attr('data-code-plugin-dirtree', null)
          .attr('data-code-plugin-params', null)
        $el.jstree({
          types: {
            babel: {icon: 'icon-file-type-babel'},
            config: {icon: 'icon-file-type-config'},
            default: {icon: 'icon-file-type-file'},
            folder: {icon: 'icon-file-type-folder'},
            js: {icon: 'icon-file-type-js'},
            json: {icon: 'icon-file-type-json'},
            md: {icon: 'icon-file-type-md'},
            npm: {icon: 'icon-file-type-npm'},
            rollup: {icon: 'icon-file-type-rollup'},
            scss: {icon: 'icon-file-type-scss'},
            vue: {icon: 'icon-file-type-vue'},
            webpack: {icon: 'icon-file-type-webpack'}
          },
          plugins: [ 'types' ],
          core: {
            themes: {name: THEME_NAME, dot: true},
            data: code
          }
        }).on('loaded.jstree', function (evt, jsTree) {
          jsTree.instance.open_all()
          $el.show()
        })
      } catch (e) {
        $el.html(e)
        $el.show()
      }
    })
  }
  require([
    'jstree/dist/jstree.min',
    'require-css/css!jstree/dist/themes/' + THEME_NAME + '/style',
    'require-css/css!mdblog-plugin-dirtree/index'
  ], function () {
    render()
  })
})
